declare namespace Geomorph {
  
  /** Generated by script `svg-meta`. */
  export type SvgJson = Record<string, ParsedSymbol<Geom.GeoJsonPolygon>>;

  export type ParsedSvgJson = Record<string, ParsedSymbol<import('../geom').Poly>>;

  export interface ParsedSymbol<T> {
    key: string;

    /** Only non-empty for hull symbols; assume connected. */
    hull: T[];
    doors: T[];
    irisValves: T[];
    labels: T[];
    obstacles: T[];
    walls: T[];

    meta: {
      /** Door titles */
      doors: (null | string)[];
      /** Only exists for hull symbols */
      hullRect?: Geom.RectJson;
      pngRect: Geom.RectJson;
      svgInnerText?: string; // TODO remove
    };
  }

  export interface SymbolLayout {
    /**
     * Corresponds to basename of original PNG,
     * e.g. `g-301--bridge` where public/debug/g-301--bridge.png exists.
     */
    key: string;
    id: number;
    items: SymbolLayoutItem[];
  }

  export interface SymbolLayoutItem {
    symbol: string;
    hull?: boolean;
    transform?: [number, number, number, number, number, number];
    tags?: string[];
  }
}
